def file_icon_demo():
    return "data:image/png;base64,"


def file_main_py(module, entrypoint):
    main_py = f"""#!/usr/bin/env python

from chariot_scaffold.runtime import Runtime
from {entrypoint} import {module}


def main():
    Runtime({module}).start()


if __name__ == '__main__':
    main()

"""
    return main_py


def file_dockerfile():
    dockerfile = """
FROM python:3.10.7-slim

WORKDIR /python/src
ADD . /python/src
RUN chmod +x /python/src/main.py

# replace apt source && config pip source
RUN  <<EOF cat>/etc/apt/sources.list
deb https://mirrors.aliyun.com/debian/ bullseye main non-free contrib
deb-src https://mirrors.aliyun.com/debian/ bullseye main non-free contrib
deb https://mirrors.aliyun.com/debian-security/ bullseye-security main
deb-src https://mirrors.aliyun.com/debian-security/ bullseye-security main
deb https://mirrors.aliyun.com/debian/ bullseye-updates main non-free contrib
deb-src https://mirrors.aliyun.com/debian/ bullseye-updates main non-free contrib
deb https://mirrors.aliyun.com/debian/ bullseye-backports main non-free contrib
deb-src https://mirrors.aliyun.com/debian/ bullseye-backports main non-free contrib
EOF
RUN apt-get update && apt-get install -y iputils-ping net-tools vim && apt-get clean

RUN pip config set global.index-url https://mirrors.aliyun.com/pypi/simple && \
    /bin/echo -e [easy_install]\\nindex-url=https://mirrors.aliyun.com/pypi/simple > ~/.pydistutils.cfg
# Install pip dependencies
RUN if [ -f requirements.txt ]; then pip install -r requirements.txt; fi

ENTRYPOINT ["python", "/python/src/main.py"]
"""
    return dockerfile


