def generate_file(module, entrypoint, path=None):
    pro_path = path if path is not None else "./"
    assert os.path.exists(pro_path), FileNotFoundError("目录不存在")

    icon_demo_b64 = file_icon_demo()
    icon_demo = open(os.path.join(pro_path, "icon.png"), 'wb')
    icon_demo.write(base64.b64decode(icon_demo_b64.replace("data:image/png;base64,", "")))
    icon_demo.close()

    main_py_str = file_main_py(module, entrypoint)
    main_py = open(os.path.join(pro_path, "main.py"), "w", encoding="utf8")
    main_py.write(main_py_str)
    main_py.close()

    dockerfile_str = file_dockerfile()
    dockerfile = open(os.path.join(pro_path, "Dockerfile"), "w")
    dockerfile.write(dockerfile_str)
    dockerfile.close()

    help_md_str = file_help_md()
    help_md = open(os.path.join(pro_path, "help.md"), "w")
    help_md.write(help_md_str)
    help_md.close()

    requirements_str = file_requirements()
    requirements = open(os.path.join(pro_path, "requirements.txt"), "w")
    requirements.write(requirements_str)
    requirements.close()

    docker_ignore_str = file_docker_ignore()
    docker_ignore = open(os.path.join(pro_path, ".dockerignore"), "w")
    docker_ignore.write(docker_ignore_str)
    docker_ignore.close()


def check_project_file(pwd_path):
    # 生成在线包之前得先使用Pack中generate_project方法生成项目目录
    log.debug("正在校验项目文件是否存在")
    dirname = os.path.dirname(pwd_path)
    project_tree = os.listdir(dirname)

    necessary_files = [
        "Dockerfile",
        "help.md",
        "icon.png",
        "main.py",
        "plugin.spec.yaml",
        "requirements.txt"
    ]

    for i in necessary_files:
        if i not in project_tree:
            raise FileNotFoundError(f"必要的项目文件缺失: {i}, 打包前请先生成项目文件。")

    if not os.path.exists(os.path.join(dirname, "build")):
        os.mkdir(os.path.join(dirname, "build"))
    log.debug("校验完毕")


def generate_online_pack(pwd_path, name, vendor, version):
    check_project_file(pwd_path)
    dirname = os.path.dirname(pwd_path)
    path_build = os.path.join(dirname, "build")
    project_tree = os.listdir(dirname)
    except_file = ["__pycache__", "build"]
    log.debug("正在打包在线插件包")
    online_tar = tarfile.open(os.path.join(path_build, f"{vendor}-{name}-{version}-online.tar.gz"), "w:gz")
    for i in project_tree:
        if i not in except_file:
            online_tar.add(os.path.join(dirname, i), arcname=i)

    online_tar.close()
    log.debug("打包完毕")


def generate_offline_pack(pwd_path, name, vendor, version, platform="linux/amd64"):
    check_project_file(pwd_path)
    dirname = os.path.dirname(pwd_path)
    path_build = os.path.join(dirname, "build")

    log.debug("检查docker环境是否存在")

    docker_version = os.popen("docker -v").read()
    assert "Docker version" in docker_version, "docker环境不存在, 无法打包插件"
    log.debug(docker_version.replace("\n", ""))

    log.debug("docker构建镜像中........")
    build_status = os.system(f"docker build --platform {platform} -t {vendor}/{name}:{version} -f {os.path.join(dirname, 'Dockerfile')} {dirname}")
    assert build_status == 0, "docker镜像构建失败, 请检查docker是否启动"
    os.system(f"docker save -o {os.path.join(path_build, 'plugin.tar.gz')} {vendor}/{name}:{version}")

    log.debug("镜像构建完毕")
    log.debug("正在打包离线插件包")
    offline_tar = tarfile.open(os.path.join(path_build, f'{vendor}-{name}-{version}-{platform.replace("/","-")}-offline.tar.gz'), "w:gz")
    offline_tar.add(os.path.join(path_build, "plugin.tar.gz"), arcname="plugin.tar.gz")
    offline_tar.add(os.path.join(dirname, "icon.png"), arcname="icon.png")
    offline_tar.add(os.path.join(dirname, "help.md"), arcname="help.md")
    offline_tar.add(os.path.join(dirname, "plugin.spec.yaml"), arcname="plugin.spec.yaml")
    offline_tar.close()
    log.debug("打包完毕")

    os.remove(os.path.join(path_build, "plugin.tar.gz"))
    log.debug("删除临时文件")


