def timestamp_to_utc_format_date_string(timestamp: int | float = None, date_format="%Y-%m-%dT%H:%M:%SZ") -> str:
    """
    时间戳转换utc时间格式, alarm构造告警时间可用
    :param timestamp: 时间戳
    :param date_format: 时间转换格式 默认指定utc时间格式 %Y-%m-%dT%H:%M:%SZ
                        特殊情况需要用北京时间映射使用该格式 %Y-%m-%dT%H:%M:%S+08:00
    """
    if timestamp is None:
        timestamp = datetime.datetime.now().timestamp()
    elif len(str(timestamp)) == 13:
        timestamp = int(timestamp / 1000)
    return datetime.datetime.fromtimestamp(int(timestamp), tz=datetime.timezone.utc).strftime(date_format)


def date_to_utc_format_date_string(date: str) -> str:
    """
    日期转换utc时间格式, alarm构造告警时间可用
    注意: date只能用北京时间转换
    """
    date = datetime.datetime.strptime(date, "%Y-%m-%d %H:%M:%S")
    return timestamp_to_utc_format_date_string(date.timestamp())


async def func_runner(func, params):
    if asyncio.iscoroutinefunction(func):
        return await func(**params)
    else:
        return func(**params)
